const getCoord = require("./getCoord");
const getWeather = require("./getWeather");

const getMeteo = (address) => {
  return getCoord(address)
    .then((coord) => getWeather(coord))
    .then(({current}) => {
      return current;
    })
    .catch((err) => {
      console.error(err);
    });
}

module.exports = {getMeteo, getCoord, getWeather};
